import React, { useState, useEffect } from 'react'

import axios from 'axios'
import DisplayCountries from './DisplayCountries.js'
const App = () => {

  let searchResults = []

  const [ searchString, setSearchString ] = useState('')

  const handleSearchChange = (event) => {
    setSearchString(event.target.value)
    console.log('@Search',event.target.value)
    axios
      .request('https://restcountries.eu/rest/v2/name/'+event.target.value)
      .then(request => {
        console.log(request)
        searchResults = request.data
      })
  }

  return (
    <div>
      <h3>Search</h3>
      <input value={searchString} onChange={handleSearchChange}/>
      <DisplayCountries countries={searchResults} />
    </div>
  )
}

export default App
