import React from 'react'

import "./DisplayPersons"
import DisplayPersons from './DisplayPersons'

const DisplayFilter = ({persons,searchString}) => {
    return (searchString === '' ? <></> : <DisplayPersons persons={persons.filter(a => a.name.toLowerCase().includes(searchString.toLowerCase))} />)
}
export default DisplayFilter
