import React, {useState} from 'react'

import Country from './Country.js'

const DisplayCountries = ({countries}) => {
    const [shownCountry,setShownCountry] = useState(-1)

    if(countries.length > 10) return <p>Too Many matches, please refine your query</p>
    if(countries.length === 0) return <p>Your search does not match any countries</p>
    if(countries.length === 1) return <Country country={countries[0]} />

    return (
        <form>
        <ul>
            {
                countries.map((country,index) => {
                    return (
                        <li key={country.name}>
                            <p>{country.name}</p>
                            <input type="checkbox" id={country.name}>Show</input>
                        </li>
                    )
                })
            }
        </ul>
        </form>
    )
}

export default DisplayCountries
