import React from 'react'
import axios from 'axios'

const DisplayWeather = ({country}) => {
    const queryWeather = (country) => {
        axios
            .request(`api.openweathermap.org/data/2.5/weather?q=${country.capital},${country.cioc}`)
        console.log(country)
    }
    return (
        <div>
            <h3>Weather</h3>
            <p>Weather Placeholder</p>
            {queryWeather(country)}
        </div>
    )
}

export default DisplayWeather
