import React, { useState } from 'react'
import Note from './components/Note'
const DisplayList = ({list}) => <ul>{list.map(item => <Note key={item.id} note={item} />)}</ul>
const App = (props) => {
  const [notes, setNotes] = useState(props.notes)

  const rows = () => notes.map(note =>
    <Note
      key={note.id}
      note={note}
    />
  )

  return (
    <div>
      <h1>Notes</h1>
      <ul>
        {rows()}
      </ul>
    </div>
  )
}

export default App
