import React from 'react'
import axios from 'axios'

const DisplayWeather = ({country}) => {
    const APIKEY="1e62eeb5997264304f629b59f8bbb34f"

    const [queryResponse, setQueryResponse] = useState([])
    const queryWeather = (country) => {
        console.log(country)
        axios
            .request(`https://api.openweathermap.org/data/2.5/weather?q=${country.capital},${country.cioc}&mode=html&appid=${APIKEY}`)
            .then(request => {
                console.log(request)
                setQueryResponse(request.data)
            })
            .catch(error => {
                console.log("Errror in API call, ",error)
                setQueryResponse(<p>Weather Query Failed</p>)
            })
    }
    queryWeather(country)
    return (
        <div>
            <h3>Weather</h3>
            {queryResponse}
        </div>
    )
}

export default DisplayWeather
